import React, { useEffect, useState } from 'react'
import { getSearch } from '../../api'
import CommonHeader from '../../component/CommonHeader'
import MyInput from '../../component/MyInput'
import { connect } from 'react-redux'
import SearchList from './SearchList'
import LoadMore from '../../component/LoadMore'

function Search(props) {

    const [list,setList] = useState([]);

    useEffect(()=>{
        getSearchList()

        async function getSearchList(){
            try{
                const res = await getSearch({city:props.city,val:props.match.params.val})
                setList(res.list)
            } catch(error) {
                console.log(error)
            }

        }
    },[props.city,props.match.params.val])

    async function getDataByPage(num){
        console.log('111111')
        try{
            const res = await getSearch({city:props.city,val:props.match.params.val,page:num})
            setList(res.list)
        } catch(error) {
            console.log(error)
        }
    }



  return (
    <div>
        {/* 公共头部 */}
        <CommonHeader>
            <MyInput val = {props.match.params.val}/>
        </CommonHeader>
        {/* 搜索列表 */}
        <SearchList list={list}/>
        {/* 加载更多 */}
        <LoadMore load={getDataByPage}/>
    </div>
  )
}

export default connect(state=>({
    city:state.city
}))(Search)
