import React, { useState } from 'react'


const inputStyle = {
    height: '1.5rem',
    width: '100%',
    borderRadius: '0.3rem',
    paddingLeft: '0.2rem'
}

export default function MyInput() {
  const [val,setVal] = useState('')

  //获取文本框的输入
  function getInput(e){
    setVal(e.target.value);
  }

  //获取键盘抬起事件
  function getKeyUp(e) {
    console.log(e)
  }

  return (
    <div>
        {/* 受控组件 
            功能：输入信息，回车，跳转页面

        */}
        <input value={val} onChange={getInput} type="text" style={inputStyle} placeholder='请输入内容' 
        onKeyUp={getKeyUp}/>
    </div>
  )
}
