import React, { useEffect, useState } from 'react'
import HomeHeader from './HomeHeader'
import MyCarousel from './../../component/MyCarousel'
import { getBanner, getHotHouse } from '../../api'
import House from './House'

import './index.less'



export default function Home() {

  let [list,setList] = useState([])

  //在生命周期中请求数据 componentDidMount()函数组件：useEffect()第二个参数[]是DidMount， 
  //不加DidMount和DidUpdate，
  //变量（变量改变就加请求）
  useEffect(()=>{

    getBannerList()
    getHotHouseList()

  } 
  , [])


  async function getBannerList(){

  }


  async function getBannerList(){
    try {
      const res = await getBanner();
      setList(res.banner);
    } catch (error) {
      console.log(error);
    }
  }

  async function getHotHouseList(){
    try {
      const res = await getHotHouse({city:'上海'});
      console.log(res);
    } catch(error){
      console.log(error);
    }
  }


  return (
    <div>
      {/* 头部区域 */}
      <HomeHeader/>
      {/* 轮播图 */}
      <MyCarousel list={list}/>

      {/* 招室友 */}
      <div className="nav">
        <div className="home-item">找舍友</div>
        <div className="home-item">宜居社区</div>
      </div>

      {/* 热门房源 */}
      <House></House>
    </div>
  )
}
